import React, { useEffect, useRef } from 'react';
import * as THREE from 'three';
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls';
import { gsap } from 'gsap';

const ThreeScene: React.FC = () => {
    const mountRef = useRef<HTMLDivElement>(null);
    const cameraRef = useRef<THREE.PerspectiveCamera | null>(null);

    useEffect(() => {
        const scene = new THREE.Scene();
        scene.background = new THREE.Color(0xffffff);

        const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
        camera.position.set(10, 5, 10);
        cameraRef.current = camera;

        const renderer = new THREE.WebGLRenderer();
        renderer.setSize(window.innerWidth, window.innerHeight);
        mountRef.current?.appendChild(renderer.domElement);

        // Main Building
        const mainBuildingGeometry = new THREE.BoxGeometry(4, 4, 4);
        const mainBuildingMaterial = new THREE.MeshStandardMaterial({ color: 0x8b4513 });
        const mainBuilding = new THREE.Mesh(mainBuildingGeometry, mainBuildingMaterial);
        mainBuilding.position.y = 2;
        scene.add(mainBuilding);

        // Roof
        const roofGeometry = new THREE.ConeGeometry(3.5, 2, 4);
        const roofMaterial = new THREE.MeshStandardMaterial({ color: 0x8b0000 });
        const roof = new THREE.Mesh(roofGeometry, roofMaterial);
        roof.position.y = 5;
        roof.rotation.y = Math.PI / 4;
        scene.add(roof);

        // Ground
        const groundGeometry = new THREE.PlaneGeometry(50, 50);
        const groundMaterial = new THREE.MeshStandardMaterial({ color: 0x228b22 }); // Green color for grass
        const ground = new THREE.Mesh(groundGeometry, groundMaterial);
        ground.rotation.x = -Math.PI / 2; // Rotate to make it horizontal
        ground.position.y = 0; // Position at y = 0
        ground.receiveShadow = true; // Enable shadow receiving if shadows are used
        scene.add(ground);

        // Lighting
        const ambientLight = new THREE.AmbientLight(0xffffff, 0.5);
        scene.add(ambientLight);

        const pointLight = new THREE.PointLight(0xffffff, 1);
        pointLight.position.set(10, 10, 10);
        scene.add(pointLight);

        // Controls with restricted vertical angle
        const controls = new OrbitControls(camera, renderer.domElement);
        controls.enableDamping = true;
        controls.dampingFactor = 0.25;

        // Restrict camera rotation so it can't go under the ground
        controls.maxPolarAngle = Math.PI / 2.2; // Limit vertical rotation to just above the ground
        controls.minDistance = 5; // Minimum zoom distance from the scene
        controls.maxDistance = 50; // Maximum zoom-out distance

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        animate();

        // Cleanup on unmount
        return () => {
            renderer.dispose();
            mountRef.current?.removeChild(renderer.domElement);
        };
    }, []);

    // Array of camera positions for smooth transition
    const cameraPositions = [
        { x: 10, y: 5, z: 10 },
        { x: 0, y: 10, z: 10 },
        { x: -10, y: 5, z: -10 },
        { x: -10, y: 5, z: -10 },
    ];
    let positionIndex = 0;

    // Function to smoothly change the camera angle using gsap
    const changeCameraAngle = () => {
        if (cameraRef.current) {
            const pos = cameraPositions[positionIndex];
            gsap.to(cameraRef.current.position, {
                x: pos.x,
                y: pos.y,
                z: pos.z,
                duration: 2,
                onUpdate: () => {
                    cameraRef.current?.lookAt(0, 0, 0);
                }
            });

            positionIndex = (positionIndex + 1) % cameraPositions.length;
        }
    };

    return (
        <div>
            <button onClick={changeCameraAngle} style={{ position: 'absolute', zIndex: 1, top: 10, left: 10 }}>
                Change Camera Angle
            </button>
            <div ref={mountRef} />
        </div>
    );
};

export default ThreeScene;
