// src/ThreeScene.tsx
import React, { useEffect, useRef } from 'react';
import * as THREE from 'three';
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls';
import { gsap } from 'gsap';



const ThreeScene: React.FC = () => {
    const mountRef = useRef<HTMLDivElement>(null);


    useEffect(() => {
        const scene = new THREE.Scene();


        // Set the background color of the scene to white
scene.background = new THREE.Color(0xffffff); // 0xffffff is the hex code for white



        const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
        const renderer = new THREE.WebGLRenderer();
        const controls = new OrbitControls(camera, renderer.domElement);
        controls.enableDamping = true; // Smooth control
        controls.dampingFactor = 0.25;


        for (let i = 0; i < 5; i++) {
            const geometry = new THREE.SphereGeometry(0.5, 32, 32);
            const material = new THREE.MeshStandardMaterial({ color: Math.random() * 0xffffff });
            const sphere = new THREE.Mesh(geometry, material);

            sphere.position.set((Math.random() - 0.5) * 10, (Math.random() - 0.5) * 10, (Math.random() - 0.5) * 10);
            scene.add(sphere);
        }


        renderer.setSize(window.innerWidth, window.innerHeight);
        mountRef.current?.appendChild(renderer.domElement);

        const geometry = new THREE.BoxGeometry();
        const material = new THREE.MeshBasicMaterial({ color: 0x00ff00 });
        const cube = new THREE.Mesh(geometry, material);


        // Add this after creating the cube
        gsap.to(cube.scale, { x: 2, y: 2, z: 2, duration: 1, yoyo: true, repeat: -1 });


        renderer.shadowMap.enabled = true;

        const light = new THREE.DirectionalLight(0xffffff, 1);
        light.position.set(5, 5, 5);
        light.castShadow = true;
        scene.add(light);

        cube.castShadow = true;
        cube.receiveShadow = true;

        window.addEventListener('resize', () => {
            renderer.setSize(window.innerWidth, window.innerHeight);
            camera.aspect = window.innerWidth / window.innerHeight;
            camera.updateProjectionMatrix();
        });



        scene.add(cube);

        camera.position.z = 5;

        function animate() {
            requestAnimationFrame(animate);
            cube.rotation.x += 0.01;
            cube.rotation.y += 0.01;

            controls.update(); // Update controls
            renderer.render(scene, camera);
        }        

        animate();

        // Cleanup function to remove renderer on unmount
        return () => {
            renderer.dispose();
            mountRef.current?.removeChild(renderer.domElement);
        };
    }, []);

    return <div ref={mountRef} />;
};

export default ThreeScene;
