import React, { useEffect, useRef } from 'react';
import * as THREE from 'three';
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls';
import { FontLoader } from 'three/examples/jsm/loaders/FontLoader';
import { TextGeometry } from 'three/examples/jsm/geometries/TextGeometry';
import { Reflector } from 'three/examples/jsm/objects/Reflector'; // Import Reflector
import { gsap } from 'gsap';

const ThreeScene: React.FC = () => {
    const mountRef = useRef<HTMLDivElement>(null);
    const cameraRef = useRef<THREE.PerspectiveCamera | null>(null);
    const raycaster = useRef(new THREE.Raycaster());
    const mouse = useRef(new THREE.Vector2());

    useEffect(() => {
        const scene = new THREE.Scene();
        scene.background = new THREE.Color(0xffffff);

        const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
        camera.position.set(-5, 5, 10);
        cameraRef.current = camera;

        const renderer = new THREE.WebGLRenderer();
        renderer.setSize(window.innerWidth, window.innerHeight);
        mountRef.current?.appendChild(renderer.domElement);

        // Main Building
        const mainBuildingGeometry = new THREE.BoxGeometry(4, 4, 4);
        const mainBuildingMaterial = new THREE.MeshStandardMaterial({ color: 0x8b4513 });
        const mainBuilding = new THREE.Mesh(mainBuildingGeometry, mainBuildingMaterial);
        mainBuilding.position.y = 2;
        scene.add(mainBuilding);

        // Roof
        const roofGeometry = new THREE.ConeGeometry(3.5, 2, 4);
        const roofMaterial = new THREE.MeshStandardMaterial({ color: 0x8b0000 });
        const roof = new THREE.Mesh(roofGeometry, roofMaterial);
        roof.position.y = 5;
        roof.rotation.y = Math.PI / 4;
        scene.add(roof);

        // Sol avec Effet Miroir
        // Sol avec Effet Miroir
        const groundGeometry = new THREE.PlaneGeometry(500, 500);
        const groundMirror = new Reflector(groundGeometry, {
            color: 0x888888, // Couleur grise pour le sol miroir
            textureWidth: window.innerWidth * 2,
            textureHeight: window.innerHeight * 2,
            clipBias: 0.003 // Évite les artefacts de réflexion
        });
        groundMirror.rotation.x = -Math.PI / 2;
        groundMirror.position.y = 0;
        scene.add(groundMirror);


        // Lighting
        const ambientLight = new THREE.AmbientLight(0xffffff, 0.5);
        scene.add(ambientLight);

        const pointLight = new THREE.PointLight(0xffffff, 1);
        pointLight.position.set(10, 10, 10);
        scene.add(pointLight);

        // 3D Panel for Camera Control
        const panelGeometry = new THREE.PlaneGeometry(2, 2);
        const panelMaterial = new THREE.MeshBasicMaterial({ color: 0x333333, side: THREE.DoubleSide });
        const panel = new THREE.Mesh(panelGeometry, panelMaterial);
        panel.position.set(-5, 3, 0);
        scene.add(panel);

        // Buttons on the Panel
        const buttonGeometry = new THREE.BoxGeometry(0.6, 0.25, 0);
        const buttonMaterial = new THREE.MeshBasicMaterial({ color: 0xffffff });
        const button1 = new THREE.Mesh(buttonGeometry, buttonMaterial);
        button1.position.set(-0.5, 0.5, 0.001);
        panel.add(button1);

        const button2 = new THREE.Mesh(buttonGeometry, buttonMaterial);
        button2.position.set(0.5, 0.5, 0.001);
        panel.add(button2);

        // Load Font and Add Text to Buttons
        const fontLoader = new FontLoader();
        fontLoader.load(
            'https://threejs.org/examples/fonts/helvetiker_regular.typeface.json', // Utilise un fichier JSON de police
            (font) => {
                // Text for Button 1
                const textGeometry1 = new TextGeometry('Projet', {
                    font: font,
                    size: 0.1,
                    height: 0.02,
                });
                const textMaterial = new THREE.MeshBasicMaterial({ color: 0x000000 });
                const text1 = new THREE.Mesh(textGeometry1, textMaterial);
                text1.position.set(-0.7, 0.4, 0.001); // Position au-dessus du button1
                panel.add(text1);

                // Text for Button 2
                const textGeometry2 = new TextGeometry('Ecole', {
                    font: font,
                    size: 0.1,
                    height: 0.02,
                });
                const text2 = new THREE.Mesh(textGeometry2, textMaterial);
                text2.position.set(0.3, 0.4, 0.001); // Position au-dessus du button2
                panel.add(text2);
            }
        );

        // Array of camera positions for smooth transition
        const cameraPositions = [
            { x: 10, y: 5, z: 10 },
            { x: -10, y: 5, z: -10 },
        ];
        let positionIndex = 0;

        // Function to change camera angle
        const changeCameraAngle = (index: number) => {
            if (cameraRef.current) {
                const pos = cameraPositions[index];
                gsap.to(cameraRef.current.position, {
                    x: pos.x,
                    y: pos.y,
                    z: pos.z,
                    duration: 2,
                    onUpdate: () => {
                        cameraRef.current?.lookAt(0, 0, 0);
                    }
                });
            }
        };

        // Detect Mouse Clicks on Panel Buttons
        const onMouseClick = (event: MouseEvent) => {
            if (!mountRef.current) return;

            mouse.current.x = (event.clientX / window.innerWidth) * 2 - 1;
            mouse.current.y = -(event.clientY / window.innerHeight) * 2 + 1;

            raycaster.current.setFromCamera(mouse.current, cameraRef.current!);
            const intersects = raycaster.current.intersectObjects([button1, button2]);

            if (intersects.length > 0) {
                const clickedObject = intersects[0].object;

                if (clickedObject === button1) {
                    changeCameraAngle(0);
                } else if (clickedObject === button2) {
                    changeCameraAngle(1);
                }
            }
        };

        window.addEventListener('click', onMouseClick);

        // Orbit Controls
        const controls = new OrbitControls(camera, renderer.domElement);
        controls.enableDamping = true;
        controls.dampingFactor = 0.25;
        controls.maxPolarAngle = Math.PI / 2.2;
        controls.minDistance = 5;
        controls.maxDistance = 50;

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        animate();

        // Cleanup on unmount
        return () => {
            renderer.dispose();
            mountRef.current?.removeChild(renderer.domElement);
            window.removeEventListener('click', onMouseClick);
        };
    }, []);

    return <div ref={mountRef} />;
};

export default ThreeScene;
